
   function load() {
       var count= 0;
       var x = myFunction(2, 6);
       var random = Math.sqrt(x)*2000;
       setInterval(function() {
         $('.contaner').empty();
         for(var i =0; i< x; i++){
         var c = '#'+Math.floor(Math.random()*16777215).toString(16);
          $('.contaner').append($('<div class="smallcontener" style = "background-color:' + c +';height: '+ (400/Math.sqrt(x))  + 'px' +'; width: '+ (400/Math.sqrt(x))  + 'px' +'";></div>'))
        }

        $('.smallcontener').one('click', function(e){
          count++;
          $(this).css({"background-color": "white"});
          if (count==x){
            $('.contaner').empty();
            $('.contaner').html("<p>You Win!</p>");
            $("p").css({"text-align": "center", "font-size": "200%", "margin-top": "170px"});
            count = 0;
          }
        });
         }, random);
      }

    function myFunction(min, max) {
    var random=Math.floor(Math.random() * (max - min) + min);
    return random * random;
    }
